// archivo: server.js
const express = require("express");
const mysql = require("mysql2");
const app = express();

app.use(express.json());

// conexión a MySQL
const db = mysql.createConnection({
  host: "localhost",   // o la IP del servidor en la nube
  user: "root",
  password: "",
  database: "floresdb"
});

// endpoint para registrar datos
app.post("/api/registrar", (req, res) => {
  const { variedad, bloque, tallos } = req.body;
  const sql = "INSERT INTO registros (variedad, bloque, tallos) VALUES (?, ?, ?)";
  db.query(sql, [variedad, bloque, tallos], (err, result) => {
    if (err) {
      console.error(err);
      return res.status(500).json({ mensaje: "Error al guardar" });
    }
    res.json({ mensaje: "Registro guardado", id: result.insertId });
  });
});

// iniciar servidor
app.listen(3000, () => {
  console.log("Servidor corriendo en http://localhost:3000");
});
app.get("/api/registrar", (req, res) => {
  const { variedad, bloque, tallos } = req.query;

  if (!variedad || !bloque || !tallos) {
    return res.status(400).json({ mensaje: "Faltan datos" });
  }

  const sql = "INSERT INTO registros (variedad, bloque, tallos) VALUES (?, ?, ?)";
  db.query(sql, [variedad, bloque, tallos], (err, result) => {
    if (err) {
      console.error("❌ Error al guardar:", err);
      return res.status(500).json({ mensaje: "Error al guardar" });
    }
    res.json({ mensaje: "Registro guardado ✅", id: result.insertId });
  });
});